import React from "react";
import { useSelector } from "react-redux";
import {useEffect,useState} from 'react';
import { useDispatch } from "react-redux";
import Table from "./patientTabel";
import PatientForm from "./patientForm";
import {populatePatientDetail} from"../redux/patientDetail";
import './button.css'
import {
  Dialog,
  DialogContent,
  DialogTitle
}from '@mui/material'
import { Button } from "@material-ui/core";

function Dashboard() {
    const dispatch = useDispatch()
    const { user } = useSelector((state) => state.user)
    const [openDialog,setopenDialog] = useState(false);
    const [dataLoaded, setDataLoaded] = useState(false);
    const doctorId = user.doctorId
    useEffect(() => {
      console.log("////////",doctorId)
      const getPeopleArr = async () => {
          
        const response = await fetch(`http://localhost:8000/patient/${doctorId}`, {   
          // Adding method type
          method: "GET",
          credentials: "include",
          headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
      })
      const data = await response.json();  
      dispatch(populatePatientDetail(data))
      await getPeopleArr();
      setDataLoaded(true);
      };
      
        

        
      });
      const openSavePatientHAndler = value => {
        //value.preventDefault();
        setopenDialog(true)
        
      }
      const closePatientDialog = value => {
        //value.preventDefault();
        setopenDialog(false)
        window.location.reload();
      }
  return (
    <>
    <div>
      
      <div style={{marginTop:"50px"}}>
        { dataLoaded && <Table dialogHandler={openSavePatientHAndler}  openDialog={openDialog}  />}
        
      </div>
      <div>
        <button onClick={openSavePatientHAndler}>clcik me</button>
      </div>
      <Dialog open={openDialog} PaperProps={{ style: {
        minHeight: '90%',
        maxHeight: '90%',
        minWidth: '900px'
      }}}>
        <DialogTitle>
          <div style={{justifyContent:"space-between",display:"flex"}}>
          <p>Patient Create Form</p>
          <Button onClick={closePatientDialog} className="CloseButton">close</Button>
          </div>
        </DialogTitle>
        <DialogContent>
          <PatientForm closeHandler={closePatientDialog} />
        </DialogContent>
      </Dialog>
    </div>
    </>
  );
}

export default Dashboard;
