import React, { useRef } from "react";

function SingUp() {
    const userEMail = useRef()
    const userPassWord = useRef()
    const userConfirmPassword = useRef()
    const userName = useRef()
    function SignUpAuth(e){
        const email= userEMail.current.value
        const username= userName.current.value
        const pass1 = userPassWord.current.value
        const pass2 = userConfirmPassword.current.value
        console.log("////////",email,pass1,pass2)
        
        fetch("http://localhost:8000/user/signUp", {   
            // Adding method type
            method: "POST",
            // Adding body or contents to send
            body: JSON.stringify({
                email: email,
                password: pass1,
                username: username
            }),
            // Adding headers to the request
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
        .then(response => response.json())
        .then(json => console.log(json));
    }
  return (
    <div>
      <h1>hello SInupg
      </h1>
      <input ref={userEMail} className="email" placeholder="Enter the email" type="text" ></input>
      <br></br>
      <input ref={userName} className="email" placeholder="Enter the userName" type="text" ></input>
      <br></br>
      <input ref={userPassWord} className="password" placeholder="Enter the password" type="text" ></input>
      <hr></hr>
      <input ref={userConfirmPassword} className="confirmPassword" placeholder="Enter the password" type="text" ></input>
      <hr></hr>
      <button className='AddTaskButton' onClick={SignUpAuth}> Add Task</button>
    </div>
  );
}

export default SingUp;
