import {  TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import React, {useState } from 'react';
import CountrySelect from './countrySelect';
import './App.css';

const useStyle = makeStyles( theme => ({
    nameField:{
        "& .MuiFormControl-root":{
            width: '12vw',
            margin: '5px',
            height: '5px'
        }
    }
}))
const initialValues={
    Firstname: "",
    Lastname: "",
    Middlename: "",
    phoneNumber: "",
    email: "",
    Address: "",
    weight: 0,
    height: 0
}

function PatientForm() {
    const [values,setValues] = useState(initialValues);
    const classes = useStyle();
    const handleInputChange = e => {
        var name = e.target.name
        var value = e.target.value
        setValues({
            ...values,
            [name]:value
        })
    }
  return (
    <form>
        <div className={classes.nameField} style={{display:'flex'}}>
            <h1 style={{marginTop:"5px",marginRight:'15px'}}>Name :</h1>
            <TextField
            size="small"
            variant='standard'
            label="First name"
            name="Firstname"
            value={values.Firstname}
            onChange={handleInputChange}
            />
            <TextField
            size="small"
            variant='standard'
            label="Last name"
            name="Lastname"
            value={values.Lastname}
            onChange={handleInputChange}
            />
            <TextField
            size="small"
            variant='standard'
            label="Middle name"
            name="Middlename"
            value={values.Middlename}
            onChange={handleInputChange}
            />
        </div>
        <div style={{marginTop:'45px',display:'flex'}}>
        <h1 style={{marginTop:"5px",marginRight:'15px'}}>Email :</h1>
        <TextField
            variant='standard'
            size="small"
            label="Email"
            name="email"
            value={values.email}
            onChange={handleInputChange}
            />
        </div>
        <div style={{marginTop:'45px',display:'flex'}}>
        <h1 style={{marginRight:'15px'}}>Country :</h1>
            <CountrySelect />
        </div>
    </form>
    
  );
}

export default PatientForm;
