import React, { useRef } from "react";
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
function SingUp() {
  const theme = createTheme();
    const userEMail = useRef()
    const userPassWord = useRef()
    const userConfirmPassword = useRef()
    const userName = useRef()
    function SignUpAuth(e){
      console.log("clicked")
        const email= userEMail.current.value
        const username= userName.current.value
        const pass1 = userPassWord.current.value
        const pass2 = userConfirmPassword.current.value
        var emailFlag = false
        var passwordConditionFlag = false
        var passwordMatchingFlag = false
        var lowerCaseLetters = /[a-z]/g;
        var upperCaseLetters = /[A-Z]/g;
        var numbers = /[0-9]/g;
        // eslint-disable-next-line
        var mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        if(email.match(mailformat))
        {
          emailFlag=true
        }
        else
        {
         emailFlag = false
        }
        if(pass1 === pass2){
          passwordMatchingFlag = true
          if(pass1.length > 8  && pass1.match(lowerCaseLetters) && pass1.match(upperCaseLetters) && pass1.match(numbers)){
            passwordConditionFlag = true
          }
          else{
            passwordConditionFlag = false
          }
        }
        else{
          passwordMatchingFlag = false
        }
        if(emailFlag && passwordConditionFlag && passwordMatchingFlag){
          fetch("http://localhost:8000/user/signUp", {   
              // Adding method type
              method: "POST",
              // Adding body or contents to send
              body: JSON.stringify({
                  email: email,
                  password: pass1,
                  username: username
              }),
              // Adding headers to the request
              headers: {
                  "Content-type": "application/json; charset=UTF-8"
              }
          })
          .then(response => response.json())
          .then(json => console.log(json));
        }else{
          console.log("validation failed")
        }
    }
  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign in
          </Typography>
          <Box component="form"noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
            />
            <TextField
              
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <Button
              onClick={SignUpAuth} 
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign In
            </Button>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default SingUp;
