import { Grid, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import React, {useState, useEffect } from 'react';
import './App.css';

const useStyle = makeStyles( theme => ({
    root:{
        "& .MuiFormControl-root":{
            
            width: '15vw',
            margin: '5px'
        }
    }
}))
const initialValues={
    Firstname: "",
    Lastname: "",
    Middlename: "",
    phoneNumber: "",
    emai: "",
    Address: "",
    weight: 0,
    height: 0
}

function PatientForm() {
    const [values,setValues] = useState(initialValues);
    const[inputValue,setInputValue] = useState('');
    const classes = useStyle();
    const handleInputChange = e => {
        setInputValue(prev => prev = e.target.value)
        console.log("fieldInput Value:",inputValue)
        setValues({
            ...values,
            [e.target.name]:inputValue
        })
        console.log("final value",values)
    }
  return (
    <form className={classes.root}>
        <Grid container>
            <Grid item>
                <TextField
                variant='outlined'
                label="First name"
                name="Firstname"
                value={inputValue}
                onChange={handleInputChange}
                />
                <TextField
                variant='outlined'
                label="Last name"
                name="Lastname"
                value={inputValue}
                onChange={handleInputChange}
                />
                <TextField
                variant='outlined'
                label="Middle name"
                name="Middlename"
                value={inputValue}
                />
            </Grid>
        </Grid>
    </form>
    
  );
}

export default PatientForm;
