import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  patient: [],
}

export const PatientSlice = createSlice({
  name: 'patient',
  initialState,
  reducers: {
    populatePatientDetail: (state, action) => {
     
      state.patient = JSON.parse(JSON.stringify(action.payload));
    },
    createNewPatient: (state, action) => {
      console.log("before creating patient in store:",action.payloady)
      //state.patient = state.patient.push(action.payload);
      console.log("after creating patient in store:",state.patient)
    }
  },
})

// Action creators are generated for each case reducer function
export const { populatePatientDetail,createNewPatient } = PatientSlice.actions

export default PatientSlice.reducer
