import React from "react";
import { useSelector } from "react-redux";
import {useEffect,useState} from 'react';
import { useDispatch } from "react-redux";
//import Table from "./patientTabel";
import PatientForm from "./patientForm";
import {populatePatientDetail} from"../redux/patientDetail";
import {
  Dialog,
  DialogContent,
  DialogTitle,
  DialogContentText,DialogActions
}from '@mui/material'
import { Button } from "@material-ui/core";
function Dashboard() {
    const dispatch = useDispatch()
    const { user } = useSelector((state) => state.user)
    const [openDialog,setopenDialog] = useState(false);
    const doctorId = user.doctorId
    useEffect(() => {
      console.log("////////",doctorId)
        fetch(`http://localhost:8000/patient/${doctorId}`, {   
            // Adding method type
            method: "GET",
            credentials: "include",
            headers: {
              "Content-type": "application/json; charset=UTF-8"
          }
        })
        .then(response => response.json())
        .then(json => {
          console.log("patient details:",json)
          dispatch(populatePatientDetail(json))
        });

          
      });
      const openSavePatientHAndler = value => {
        value.preventDefault();
        setopenDialog(true)
        
      }
  return (
    <>
    <div>
      
      <div style={{marginTop:"50px"}}>
        
      </div>
      <div>
        <button onClick={openSavePatientHAndler}>clcik me</button>
      </div>
      <Dialog open={openDialog} PaperProps={{ style: {
    minHeight: '90%',
    maxHeight: '90%',
    minWidth: '900px'
  }}}>
        <DialogTitle>
          Patient Form
        </DialogTitle>
        <DialogContent>
          <PatientForm />
        </DialogContent>
        <DialogActions>
          <Button>Cancel</Button>
          <Button>Add</Button>
        </DialogActions>
      </Dialog>
    </div>
    </>
  );
}

export default Dashboard;
