import React, { useRef } from "react";
import { useSelector } from "react-redux";
import {useEffect} from 'react';
import Table from "./patientTabel";
import { populateUserDetail } from "../redux/userDetail";
import { useDispatch } from "react-redux";
//import { json } from "react-router-dom";
function Dashboard() { 
    const dataFetchedRef = useRef(false);
    const { user } = useSelector((state) => state.user)
    const dispatch = useDispatch()
    //const doctorId = user.doctorId
    useEffect(() => {
      const data = localStorage.getItem("userDetails")
      dispatch(populateUserDetail(JSON.parse(data)))
      console.log("***************",data)
      
    }, [dispatch])
    useEffect(() => {
        if (dataFetchedRef.current) return;
        dataFetchedRef.current = true;
        localStorage.setItem("userDetails",JSON.stringify(user));
        console.log("huloooooooooooooo",user)

        //fetch("http://localhost:8000/patient", {   
        //    // Adding method type
        //    method: "GET",
        //    credentials: "include",
        //    // Adding body or contents to send
        //    headers: {
        //      "doctorId":user.doctorId
        //    }
        //})
        //.then(response => response.json())
        //.then(json => console.log("karen",json));
      },[user]);
  return (
    <div>
      <h1>hello Dashboard
      </h1>
      <div>
        <Table />
      </div>
      
    </div>
  );
}

export default Dashboard;
