import React, { useRef } from "react";
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { decrement } from "../redux/userDetail";
function LogIn() {
    const { user } = useSelector((state) => state.user)
    const dispatch = useDispatch()
    const navigate = useNavigate();
    const userEMail = useRef()
    const userPassWord = useRef()
    async function logInAuth(e){
        const email= userEMail.current.value
        const password = userPassWord.current.value
        console.log("////////",email,password)
        var UserDetails = undefined;
        await fetch("http://localhost:8000/login", {   
            // Adding method type
            method: "POST",
            credentials: "include",
            // Adding body or contents to send
            body: JSON.stringify({
                username: email,
                password: password
            }),
            // Adding headers to the request
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
        .then(response => response.json())
        .then(json => UserDetails = json);
        if(UserDetails && UserDetails.message === "Authenticated"){
          console.log("*************************",UserDetails)
          dispatch(decrement(UserDetails))
          navigate('/Dashboard');
        }
        else{
          alert("mofo")
        }
    }
  return (
    <div>
      <h1>hello Loogi
      </h1>
      <input ref={userEMail} className="email" placeholder="Enter the email" type="text" ></input>
      <br></br>
      <input ref={userPassWord} className="password" placeholder="Enter the password" type="text" ></input>
      <hr></hr>
      <button className='AddTaskButton' onClick={logInAuth}> Add Task</button>
    </div>
  );
}

export default LogIn;
