import React from "react";
import {useEffect,useState} from 'react';
import MaterialTable from 'material-table';
import { useSelector } from "react-redux";
function Table() {
    const { patient } = useSelector((state) => state.patient)
    var patientList = patient.map(patient => ({ ...patient}));
    const [data, setData] = useState(patientList)
    const { user } = useSelector((state) => state.user)
    const doctorJwt = user.access_token

    useEffect(() => {
        console.log("onlygets called if there is a change in table")
    },[setData])
    useEffect(() => {
        console.log("checking patient store:",patient)
        let clonedArray = JSON.parse(JSON.stringify(patient))
        clonedArray.forEach(function (arrayItem) {
           arrayItem.Fullname = arrayItem.Firstname +" " + arrayItem.Lastname;
        });
        console.log("fullname:",clonedArray)
    });
    //const data = [ {name:"kamalesh",age:24},
    //{name:"magesh",age:24},
    //{name:"nandy",age:21},
    //{name:"vinmeetha",age:28} ]
    
    const columns = [
        { title: 'Sl no', field: 'tableData.id', render:rowData => rowData.tableData.id+1} ,
        {
            title:'name',
            field: 'Firstname',
            render: (row)=> row.Firstname +" " + row.Lastname
        },
        //{
        //    title: 'Name',
        //    render: patient => `${patient.Firstname} ${patient.Lastname}`,
        //    customFilterAndSearch: (term, patient) => patient.Firstname.toLowerCase().indexOf(term.toLowerCase()) !== -1
        //},
        {
            title:"Email",field:"email"
        },
        {
            title:"PhoneNumber",field:"phoneNumber"
        },
        {
            title:"Address",field:"Address"
        }
    ]
    async function checkAuth(e){
        var responseCopy;
        console.log("checking AUth")
        await fetch("http://localhost:8000/protected", {
            method: 'GET',
            headers: {
                'Content-type': 'application/json',
                'Authorization': `Bearer ${doctorJwt}`, // notice the Bearer before your token
            }
        }).then(response => response.json())
        .then(json =>  responseCopy = json);
        if(responseCopy.message === "Authentic User"){
            return true
        }
        else{
            return false
        }    
    }
    async function deletePatient(patientId){
        var delObj;
        await fetch(`http://localhost:8000/patient/${patientId}`, {
            method: 'DELETE',
            credentials: "include",
        }).then(response => response.json())
        .then(json =>  delObj = json)
        return delObj;
    }
    return (
      <>
        <h1>table goes here</h1>
        <div style={{width:"50 vw"}}>
            <MaterialTable title="Material Table"
            data={patientList}
            columns={columns}
            options={{
                paging:false,
                actionsColumnIndex: -1, addRowPosition: "first"
            }}
            editable={{
                onRowDelete:  selectedRow =>  new Promise((resolve,reject) => {
                    const queryObj = JSON.stringify({
                        patientId:selectedRow._id
                    })
                    console.log("query Obj:",queryObj)
                    var authenticated = checkAuth(doctorJwt).then(function(result) {
                        console.log(result)
                     })
                    
                    if(authenticated){
                        deletePatient(selectedRow._id).then(function(result) {
                            console.log("%%%%%%%%%%%",result)
                            if(result.error === "Not Found" && result.statusCode === 404){
                                alert("patient Not found")
                            }else if(result.message === "Deletion Success") {
                                console.log("patient found",result.Response.Firstname ,result.Response.Lastname)
                                var newData = data.filter((item) => item._id !== result.Response._id);
                                
                                setTimeout(() => {
                                    setData(newData)
                                    resolve()
                                }, 2000)
                            }
                         })
                        
                    }
                    else{
                        console.log("failed")
                    }
                })
            }}
            />
        </div>
      </>
    );
  }
  
  export default Table;
