import { Grid, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import React, {useState, useEffect } from 'react';
import './App.css';

const useStyle = makeStyles( theme => ({
    nameField:{
        "& .MuiFormControl-root":{
            width: '12vw',
            margin: '5px',
            height: '5px'
        }
    }
}))
const initialValues={
    Firstname: "",
    Lastname: "",
    Middlename: "",
    phoneNumber: "",
    email: "",
    Address: "",
    weight: 0,
    height: 0
}

function PatientForm() {
    const [values,setValues] = useState(initialValues);
    const classes = useStyle();
    const handleInputChange = e => {
        var name = e.target.name
        var value = e.target.value
        setValues({
            ...values,
            [name]:value
        })
    }
  return (
    <form>
        <div className={classes.nameField} style={{display:'flex'}}>
            <h1 style={{marginTop:"5px",marginRight:'15px'}}>Name :</h1>
            <TextField
            variant='outlined'
            label="First name"
            name="Firstname"
            value={values.Firstname}
            onChange={handleInputChange}
            />
            <TextField
            variant='outlined'
            label="Last name"
            name="Lastname"
            value={values.Lastname}
            onChange={handleInputChange}
            />
            <TextField
            variant='outlined'
            label="Middle name"
            name="Middlename"
            value={values.Middlename}
            onChange={handleInputChange}
            />
        </div>
        <div style={{marginTop:'15px;'}}>
        <TextField
            variant='outlined'
            label="Email"
            name="email"
            value={values.email}
            onChange={handleInputChange}
            />
        </div>
    </form>
    
  );
}

export default PatientForm;
