import React, { useRef } from "react";
import Cookies from 'js-cookie';
function LogIn() {
    const userEMail = useRef()
    const userPassWord = useRef()
    function logInAuth(e){
        const email= userEMail.current.value
        const password = userPassWord.current.value
        console.log("////////",email,password)
        fetch("http://localhost:8000/login", {   
            // Adding method type
            method: "POST",
            // Adding body or contents to send
            body: JSON.stringify({
                username: email,
                password: password
            }),
            // Adding headers to the request
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
        .then(response => response.json())
        .then(json => console.log(json))
        .then(console.log("*********************",Cookies.get('username'))
          );
    }
  return (
    <div>
      <h1>hello Loogi
      </h1>
      <input ref={userEMail} className="email" placeholder="Enter the email" type="text" ></input>
      <br></br>
      <input ref={userPassWord} className="password" placeholder="Enter the password" type="text" ></input>
      <hr></hr>
      <button className='AddTaskButton' onClick={logInAuth}> Add Task</button>
    </div>
  );
}

export default LogIn;
