import {  TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import Select from 'react-select'
import {React, useMemo , useState } from 'react';
import './App.css';
import PhoneInput, { isValidPhoneNumber } from 'react-phone-number-input'
import countryList from 'react-select-country-list'
import 'react-phone-number-input/style.css'
import './phoneNumber.css'
const useStyle = makeStyles( theme => ({
    nameField:{
        "& .MuiFormControl-root":{
            width: '12vw',
            margin: '5px',
            height: '5px'
        }
    }
}))
const initialValues={
    Firstname: "",
    Lastname: "",
    Middlename: "",
    phoneNumber: "",
    email: "",
    Address: "",
    weight: 0,
    height: 0
}

function PatientForm() {
    const [value, setValue] = useState()
    const [values,setValues] = useState(initialValues);
    const [address,setAddress] = useState('');
    const [state,setState] = useState('');
    const [zip_code,setZip_code] = useState('');
    const [Countryvalue, setCountryvalue] = useState('')
    const countryOptions = useMemo(() => countryList().getData(), [])
    const classes = useStyle();
    const handleInputChange = e => {
        var name = e.target.name
        var value = e.target.value
        setValues({
            ...values,
            [name]:value
        })
    }
    const countrySelectHandler = value => {
        setCountryvalue(value)
    }
    const addressInputHandler = e => {
        var value = e.target.value
        console.log('josee',value)
        setAddress(value)
        setValues({
            ...values,
            Address:value
        })
        
    }
    const formSubmitHandler = value => {
      value.preventDefault();
      console.log("final filed values:", address,Countryvalue)

      //fetch("http://localhost:8000/patient", {   
      //        // Adding method type
      //        method: "POST",
      //        // Adding body or contents to send
      //        body: JSON.stringify({
      //          Firstname:"prakee",
      //          Lastname:"Manasddsasdo",
      //          Middlename:"abi",
      //          phoneNumber:"756259955",
      //          Address:address + Countryvalue.label,
      //          weight:75,
      //          height:175,
      //          Doctor:"635dba395447d80c71395d0f",
      //          email:"prakeeth@gmail.com"
      //      }),
      //        // Adding headers to the request
      //        headers: {
      //            "Content-type": "application/json; charset=UTF-8"
      //        }
      //    })
      //    .then(response => response.json())
      //    .then(json => console.log(json));
      
    }
  return (
    <form>
        <h1 style={{marginTop:"10px",marginRight:'15px'}}>Name</h1>
        <div className={classes.nameField} style={{display:'flex'}}>
            
            <TextField
            required
            size="small"
            variant='standard'
            label="First name"
            name="Firstname"
            value={values.Firstname}
            onChange={handleInputChange}
            />
            <TextField
            size="small"
            variant='standard'
            label="Last name"
            name="Lastname"
            value={values.Lastname}
            onChange={handleInputChange}
            />
            <TextField
            size="small"
            variant='standard'
            label="Middle name"
            name="Middlename"
            value={values.Middlename}
            onChange={handleInputChange}
            />
        </div>
        <div  style={{marginTop:'45px',display:'flex'}}>
        <h1 style={{marginTop:"5px",marginRight:'15px'}}>Email :</h1>
        <TextField
            variant='standard'
            size="small"
            label="Email"
            name="email"
            value={values.email}
            onChange={handleInputChange}
            />
        </div>
        <div style={{marginTop:'45px',display:'flex'}}>
        <h1 style={{marginRight:'15px'}}>Address :</h1>
        <div>
            <TextField
                variant='standard'
                size="small"
                label="adress"
                name="address"
                value={address}
                onChange={addressInputHandler}
            />
         </div>
        </div>
        <div style={{marginTop:'45px',display:'flex'}}>
        <h1 style={{marginRight:'15px'}}>Country :</h1>
        <div style={{width:"150px"}}>
            <Select
            options={countryOptions} 
            value={Countryvalue} 
            onChange={countrySelectHandler}
            ></Select>
         </div>
        </div>
        <div style={{marginTop:'45px',display:'flex'}}>
        
        {Countryvalue && (
        <div>
            <h1 style={{marginRight:'15px'}}>State :</h1>
            <TextField
                variant='standard'
                size="small"
                label="state"
                name="state"
                value={state}
                onChange={value => setState(value)}
            />
            <TextField
                variant='standard'
                size="small"
                label="zip_code"
                name="zip_code"
                value={zip_code}
                onChange={value => setZip_code(value)}
            />
         </div>
        )}
        </div>
        <div style={{marginTop:'45px',display:'flex'}}>
        <h1 style={{marginRight:'15px'}}>Phone :</h1>
        <div>
            <PhoneInput
            className="PhoneInputInput"
            international
            defaultCountry={Countryvalue.value}
            value={value}
            onChange={value => setValues({...values,phoneNumber:value})} />
            <p>Is valid: {values.phoneNumber && isValidPhoneNumber(values.phoneNumber) ? 'true' : 'false'}</p>
         </div>
        </div>
        <div style={{marginTop:'95px',display:'flex'}}>
        <TextField
            type="number"
            size="small"
            variant='standard'
            label="Weight"
            name="weight"
            value={values.weight}
            onChange={handleInputChange}
            style = {{width: '100px', marginRight: '30px'}}
            />
        <TextField
            type="number"
            size="small"
            variant='standard'
            label="Height"
            name="height"
            value={values.height}
            onChange={handleInputChange}
            style = {{width: '100px'}}
            />
        </div>
        <button onClick={formSubmitHandler}>SUbmit</button>
    </form>
    
  );
}

export default PatientForm;
