import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  patient: [],
}

export const PatientSlice = createSlice({
  name: 'patient',
  initialState,
  reducers: {
    populatePatientDetail: (state, action) => {
      console.log("payload",action.payload)
      state.patient(action.payload);
      //state.patient = JSON.parse(JSON.stringify(action.payload));
    },
    createNewPatient: (state, action) => {
      console.log("check the payload:",action.payload)
      state.patient.push(action.payload);
      
    },
    updateNewPAtient:(state, action) => {
      console.log("check the payload:",action.payload)

      
    },
  },
})

// Action creators are generated for each case reducer function
export const { populatePatientDetail,createNewPatient,updateNewPAtient } = PatientSlice.actions

export default PatientSlice.reducer
